mapper.update:
UPDATE item
parent_id=#{item.parentId}, se_id=#{item.season.seasonId}, u_id=#{item.user.userId}, item_name=#{item.itemName}, item_place=#{item.itemPlace}, item_unit=#{item.itemUnit}, item_sex=#{item.itemSex}, item_amount=#{item.itemAmount}, athlete_amount=#{item.athleteAmount}, start_time=#{item.startTime}, end_time=#{item.endTime}, create_time=#{item.createTime}, edit_time=#{item.editTime}
WHERE item_id=#{item.itemId}

select.where:
AND i.item_id=#{item.itemId}
AND i.parent_id=#{item.parentId}
AND i.se_id=#{item.season.seasonId}
AND i.u_id=#{item.user.userId}
AND i.item_name =#{item.itemName}
AND i.item_place=#{item.itemPlace}
AND i.item_unit=#{item.itemUnit}
AND i.item_sex=#{item.itemSex}
AND i.item_amount=#{item.itemAmount}
AND i.athlete_amount=#{item.athleteAmount}
AND i.start_time=#{item.startTime}
AND i.end_time=#{item.endTime}
AND i.parent_id IS NOT NULL

