mapper.update:
UPDATE score

    score=#{score},
    is_break_record=#{isBreakRecord},
    create_time=#{createTime},
    edit_time=#{editTime}

where i_id=#{athlete.item.itemId} and u_id=#{athlete.user.userId}

mapper.select:
SELECT s.score_id, s.score, s.is_break_record, s.edit_time, a.athlete_id, i.item_id, i.item_name, i.item_sex, u.user_id, u.user_no, u.nickname, u.user_sex, t.team_id, t.team_name, r.ranking_id, r.rank FROM `score` s INNER JOIN `athlete` a ON s.a_id = a.athlete_id INNER JOIN `user` u ON a.u_id = u.user_id INNER JOIN `team` t ON u.t_id = t.team_id INNER JOIN `item` i ON a.i_id = i.item_id INNER JOIN `ranking` r ON s.r_id = r.ranking_id
AND u.user_id = #{score.athlete.user.userId}
AND u.nickname LIKE '%${score.athlete.user.nickname}%'
AND i.item_id = #{score.athlete.item.itemId}
ORDER BY score DESC

SELECT s.score_id, s.score, s.is_break_record, s.edit_time, i.item_id, i.item_name, i.item_sex, i.item_unit, i.athlete_amount, se.season_id, se.season_name, se.season_status, u.user_id, u.user_no, u.nickname, u.user_sex, u.t_id FROM ( `score` s INNER JOIN `user` u ON s.u_id = u.user_id ) INNER JOIN `item` i ON s.i_id = i.item_id INNER JOIN `season` se ON i.se_id = se.season_id
AND i.item_id = #{itemId}
ORDER BY s.score ${condition} Limit 3
