mapper.update:
UPDATE score

    score=#{score},
    create_time=#{createTime},
    edit_time=#{editTime}

where i_id=#{item.itemId} and u_id=#{user.userId}

mapper.select:
SELECT s.score_id, s.score, s.edit_time, i.item_id, i.item_name, i.item_sex, i.item_unit, i.athlete_amount, u.user_id, u.user_no, u.nickname, u.user_sex, u.t_id, r.rank as user_type FROM ( `score` s INNER JOIN `user` u ON s.u_id = u.user_id ) INNER JOIN `item` i ON s.i_id = i.item_id LEFT JOIN `ranking` r ON s.u_id = r.u_id AND s.i_id = r.i_id
AND u.user_id = #{score.user.userId}
AND u.nickname LIKE '%${score.user.nickname}%'
AND i.item_id = #{score.item.itemId}
ORDER BY score DESC
