mapper.update:
UPDATE score
score=#{score}, is_break_record=#{isBreakRecord}, create_time=#{createTime}, edit_time=#{editTime},
WHERE score_id=#{scoreId}

mapper.select:
SELECT s.score_id, s.score, s.is_break_record, s.edit_time, a.athlete_id, i.item_id, i.item_name, i.item_sex, u.user_id, u.user_no, u.nickname, u.user_sex, t.team_id, t.team_name FROM `score` s INNER JOIN `athlete` a ON s.a_id = a.athlete_id INNER JOIN `user` u ON a.u_id = u.user_id INNER JOIN `team` t ON u.t_id = t.team_id INNER JOIN `item` i ON a.i_id = i.item_id
AND u.user_id = #{score.athlete.user.userId}
AND u.nickname LIKE '%${score.athlete.user.nickname}%'
AND i.item_id = #{score.athlete.item.itemId} AND t.team_id = #{score.athlete.user.team.teamId}
ORDER BY score DESC
