mapper.update:
UPDATE season
season_status=#{season.seasonStatus}, season_name=#{season.seasonName}, season_topic_desc=#{season.seasonTopicDesc}, season_begin_time=#{season.seasonBeginTime}, season_end_time=#{season.seasonEndTime}, season_status=#{season.seasonStatus},
season_athlete_amount=#{season.seasonAthleteAmount}
create_time=#{season.createTime}, edit_time=#{season.editTime},
WHERE season_id=#{season.seasonId}

mapper.select:
SELECT
season_id,
season_name,
season_topic_desc,
season_begin_time,
season_end_time,
season_status,
season_athlete_amount,
create_time,
edit_time
FROM
season

    AND season_id=#{season.seasonId}
    AND season_name=#{season.seasonName}
    AND season_topic_desc=#{season.seasonTopicDesc}
    AND season_begin_time=#{season.seasonBeginTime}
    AND season_end_time=#{season.seasonEndTime}
    AND season_status=#{season.seasonStatus}
    AND
        season_athlete_amount=#{season.seasonAthleteAmount}

    AND create_time=#{season.createTime}
    AND edit_time=#{season.editTime}
